package org.jibx.ws.soap.testdata.basic;

/**
 * A person object for use in testing.
 * 
 * @author Nigel Charman
 */
public class Person
{
    public int customerNumber;
    public String firstName;
    public String lastName;

    /** Constructor. */
    public Person() {
    }

    /** 
     * Constructor.
     * @param custNumber customer number
     */
    public Person(int custNumber) {
        customerNumber = custNumber;
    }

    /**
     * {@inheritDoc}
     */
    public boolean equals(Object obj) {
        if (this == obj) {
            return true;
        }
        if (obj == null) {
            return false;
        }
        if (getClass() != obj.getClass()) {
            return false;
        }
        final Person other = (Person) obj;
        if (customerNumber != other.customerNumber) {
            return false;
        }
        if (firstName == null) {
            if (other.firstName != null) {
                return false;
            }
        } else if (!firstName.equals(other.firstName)) {
            return false;
        }
        if (lastName == null) {
            if (other.lastName != null) {
                return false;
            }
        } else if (!lastName.equals(other.lastName)) {
            return false;
        }
        return true;
    }

    /**
     * {@inheritDoc}
     */
    public int hashCode() {
        final int prime = 31;
        int result = super.hashCode();
        result = prime * result + customerNumber;
        result = prime * result + ((firstName == null) ? 0 : firstName.hashCode());
        result = prime * result + ((lastName == null) ? 0 : lastName.hashCode());
        return result;
    }
}
